<script setup>
import {defineProps} from "vue";

// 상위 컴포넌트에서 아이템 받아옴
const props = defineProps(["item"]);

// 리스트에 추가하는 이벤트 전파
const emit = defineEmits(['addToList']);
const handleClick = (event, item) =>{
  event.stopPropagation();
  console.log("addToList in AttractionDetail");
  emit('addToList', item);
}

</script>

